import logo from './logo.svg';
import './App.css';
import { useState } from 'react';

class DiceNum{
  constructor(id,num){
    this.id = id;
    this.num = num;
  }
}

function App() {

  const [diceNums, setDiceNums] = useState([new DiceNum(0,0),new DiceNum(1,1),
    new DiceNum(2,2),new DiceNum(3,3),new DiceNum(4,4),new DiceNum(5,5),
    new DiceNum(6,6),new DiceNum(7,7),new DiceNum(8,8),new DiceNum(9,9),
  ]);

  const [selected, setSelected] = useState([]);

  const handleSelect = (diceNum) =>{
    if(!selected.includes(diceNum)){
      setSelected([diceNum,...selected]);
    }
    else{
      setSelected(selected.filter((item) => item.id !== diceNum.id));
    }
  }

  return (
    <div className="app-container">
      <h1 className='main-header'>Tenzies</h1>
      <p className='instructions-p'>Roll until all dice are the same. Click each die to freeze it at its current value between rolls.</p>
      <div className='dice-container'>
        <button className='dice' id='die0' onClick={() =>handleSelect(diceNums[0])}>{diceNums[0].num}</button>
        <button className='dice' id='die1' onClick={() =>handleSelect(diceNums[1])}>{diceNums[1].num}</button>
        <button className='dice' id='die2' onClick={() =>handleSelect(diceNums[2])}>{diceNums[2].num}</button>
        <button className='dice' id='die3' onClick={() =>handleSelect(diceNums[3])}>{diceNums[3].num}</button>
        <button className='dice' id='die4' onClick={() =>handleSelect(diceNums[4])}>{diceNums[4].num}</button>
        <button className='dice' id='die5' onClick={() =>handleSelect(diceNums[5])}>{diceNums[5].num}</button>
        <button className='dice' id='die6' onClick={() =>handleSelect(diceNums[6])}>{diceNums[6].num}</button>
        <button className='dice' id='die7' onClick={() =>handleSelect(diceNums[7])}>{diceNums[7].num}</button>
        <button className='dice' id='die8' onClick={() =>handleSelect(diceNums[8])}>{diceNums[8].num}</button>
        <button className='dice' id='die9' onClick={() =>handleSelect(diceNums[9])}>{diceNums[9].num}</button>
      </div>
      <button className='roll-button'>Roll</button>
    </div>
  );
}

document.getElementById('die0').addEventListener('click', function() {
    this.style.backgroundColor = 'rgb(76, 212, 49)';
  });

export default App;
