  this.classList.toggle('clicked');
});
document.getElementById('die4')?.addEventListener('click', function() {
  this.classList.toggle('clicked');
});
document.getElementById('die5')?.addEventListener('click', function() {
  this.classList.toggle('clicked');
});
document.getElementById('die6')?.addEventListener('click', function() {
  this.classList.toggle('clicked');
});
document.getElementById('die7')?.addEventListener('click', function() {
  this.classList.toggle('clicked');
});
document.getElementById('die8')?.addEventListener('click', function() {
  this.classList.toggle('clicked');
});
document.getElementById('die9')?.addEventListener('click', function() {
  this.classList.toggle('clicked');
});

export default App;
